import React from 'react';
import { Container } from 'react-bootstrap';
import ProductList from '../components/products/ProductList';
import '../styles/productsPage.css';

const ProductsPage = () => {
  const heroHighlights = [
    { label: 'Authentic Ethiopian brands', value: '120+' },
    { label: 'Cities we deliver to', value: '45' },
    { label: 'Average delivery time', value: '48 hrs' }
  ];

  return (
    <div className="products-page-wrapper">
      <section className="products-hero text-center">
        <span className="products-pill">Curated by EthioEcommerce</span>
        <h1 className="products-title">Discover the latest treasures across Ethiopia</h1>
        <p className="products-subtitle">
          From handcrafted artisan pieces to everyday essentials, explore collections delivered to your doorstep.
        </p>
        <div className="products-hero-highlights">
          {heroHighlights.map(item => (
            <div key={item.label} className="products-hero-highlight">
              <span className="highlight-value">{item.value}</span>
              <span className="highlight-label">{item.label}</span>
            </div>
          ))}
        </div>
      </section>

      <Container className="products-container pb-5">
        <div className="products-quick-filters">
          <span className="quick-filters-label">Popular filters:</span>
          <div className="quick-filters-chips">
            {['Handmade', 'Coffee beans', 'Fashion', 'Home décor', 'Fast delivery'].map(tag => (
              <span key={tag} className="quick-filter-chip">{tag}</span>
            ))}
          </div>
        </div>

        <ProductList />
      </Container>
    </div>
  );
};

export default ProductsPage;
