import React, { createContext, useState, useContext, useEffect } from 'react';
import { toast } from 'react-toastify';  // ✅ Import toast
import 'react-toastify/dist/ReactToastify.css'; // ✅ Import CSS once in your app (or in App.jsx)

// Create context
const CartContext = createContext();

// Custom hook to use CartContext
export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

// Provider component
export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);

  // Load cart from localStorage on mount
  useEffect(() => {
    const savedCart = localStorage.getItem('ethioEcommerce_cart');
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  // Save cart to localStorage whenever cartItems change
  useEffect(() => {
    localStorage.setItem('ethioEcommerce_cart', JSON.stringify(cartItems));
  }, [cartItems]);

  // ✅ Enhanced addToCart function with toast notification
  const addToCart = (product, quantity = 1) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item._id === product._id);
      let newItems;

      if (existingItem) {
        newItems = prevItems.map(item =>
          item._id === product._id
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
      } else {
        newItems = [
          ...prevItems,
          {
            _id: product._id,
            name: product.name,
            price: product.price,
            image: product.images?.[0]?.url || '/images/placeholder.jpg',
            stock: product.stock,
            quantity: quantity
          }
        ];
      }

      // ✅ Show toast notification
      toast.success(`${quantity} ${product.name?.en || product.name} added to cart!`, {
        position: "top-right",
        autoClose: 3000,
      });

      return newItems;
    });
  };

  const removeFromCart = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item._id !== productId));
  };

  const updateQuantity = (productId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(productId);
    } else {
      setCartItems(prevItems =>
        prevItems.map(item =>
          item._id === productId ? { ...item, quantity } : item
        )
      );
    }
  };

  const clearCart = () => {
    setCartItems([]);
  };

  const getCartTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getCartItemsCount = () => {
    return cartItems.reduce((count, item) => count + item.quantity, 0);
  };

  const getCartSummary = () => {
    const subtotal = getCartTotal();
    const shipping = subtotal > 1000 ? 0 : 50; // Free shipping over 1000 ETB
    const total = subtotal + shipping;

    return {
      subtotal,
      shipping,
      total,
      itemsCount: getCartItemsCount()
    };
  };

  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartItemsCount,
    getCartSummary,
    loading
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
