import React from 'react';
import { Row, Col, Form, Button } from 'react-bootstrap';
import { useCart } from '../../contexts/CartContext';
import { useApp } from '../../contexts/AppContext';

const CartItem = ({ item }) => {
  const { updateQuantity, removeFromCart } = useCart();
  const { formatPrice, t } = useApp();

  const handleQuantityChange = (e) => {
    const newQuantity = parseInt(e.target.value);
    updateQuantity(item._id, newQuantity);
  };

  const handleRemove = () => {
    removeFromCart(item._id);
  };

  return (
    <Row className="align-items-center border-bottom pb-3 mb-3">
      <Col md={2}>
        <img 
          src={item.image} 
          alt={item.name.en} 
          className="img-fluid rounded"
          style={{ width: '80px', height: '80px', objectFit: 'cover' }}
        />
      </Col>
      
      <Col md={4}>
        <h6 className="mb-1">{item.name.en}</h6>
        {item.name.am && <small className="text-muted">{item.name.am}</small>}
      </Col>
      
      <Col md={2}>
        <span className="fw-bold">{formatPrice(item.price)}</span>
      </Col>
      
      <Col md={2}>
        <Form.Select 
          value={item.quantity} 
          onChange={handleQuantityChange}
          size="sm"
        >
          {[...Array(Math.min(item.stock, 10)).keys()].map(x => (
            <option key={x + 1} value={x + 1}>
              {x + 1}
            </option>
          ))}
        </Form.Select>
      </Col>
      
      <Col md={1}>
        <span className="fw-bold">
          {formatPrice(item.price * item.quantity)}
        </span>
      </Col>
      
      <Col md={1}>
        <Button 
          variant="outline-danger" 
          size="sm"
          onClick={handleRemove}
        >
          <i className="fas fa-trash"></i>
        </Button>
      </Col>
    </Row>
  );
};

export default CartItem;
