import React, { useState, useEffect, useCallback } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Nav, Modal, Form, Alert, Spinner } from 'react-bootstrap';
import { Link, Outlet, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useApp } from '../../contexts/AppContext';
import { sellerAPI, promotionsAPI } from '../../services/api';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import Message from '../../components/common/Message';

// Add this import
import InventoryAlerts from '../../components/alerts/InventoryAlerts';

const SellerDashboard = () => {
  const [stats, setStats] = useState({});
  const [recentOrders, setRecentOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [productsLoading, setProductsLoading] = useState(false);
  const { user } = useAuth();
  const { formatPrice } = useApp();
  const location = useLocation();
  const navigate = useNavigate();
  const [showInventoryModal, setShowInventoryModal] = useState(false);
  const [inventoryForm, setInventoryForm] = useState({ productId: '', adjustmentType: 'set', quantity: '', note: '' });
  const [inventorySubmitting, setInventorySubmitting] = useState(false);
  const [inventoryFeedback, setInventoryFeedback] = useState({ variant: '', message: '' });
  const [showPromotionModal, setShowPromotionModal] = useState(false);
  const [promotionForm, setPromotionForm] = useState({
    name: '',
    description: '',
    discountType: 'percentage',
    discountValue: '',
    startDate: '',
    endDate: '',
    productId: ''
  });
  const [promotionSubmitting, setPromotionSubmitting] = useState(false);
  const [promotionFeedback, setPromotionFeedback] = useState({ variant: '', message: '' });

  const unwrapData = useCallback((response) => response?.data?.data ?? response?.data ?? null, []);

  const extractArray = useCallback((response) => {
    const unwrapped = unwrapData(response);
    if (Array.isArray(unwrapped)) {
      return unwrapped;
    }
    if (unwrapped && typeof unwrapped === 'object') {
      const arrayValue = Object.values(unwrapped).find(Array.isArray);
      if (Array.isArray(arrayValue)) {
        return arrayValue;
      }
    }
    if (Array.isArray(response?.data)) {
      return response.data;
    }
    if (response?.data && typeof response.data === 'object') {
      const arrayValue = Object.values(response.data).find(Array.isArray);
      if (Array.isArray(arrayValue)) {
        return arrayValue;
      }
    }
    return [];
  }, [unwrapData]);

  const refreshProducts = useCallback(async () => {
    setProductsLoading(true);
    try {
      const response = await sellerAPI.getSellerProducts({ limit: 8, sort: '-createdAt' });
      const productList = extractArray(response);
      setProducts(productList.slice(0, 8));
    } catch (error) {
      console.error('Error fetching seller products:', error);
      setProducts([]);
    } finally {
      setProductsLoading(false);
    }
  }, [extractArray]);

  // Redirect if not seller
  React.useEffect(() => {
    if (user && user.role !== 'seller') {
      navigate('/');
    }
  }, [user, navigate]);

  const fetchDashboardData = useCallback(async () => {
    setLoading(true);
    try {
      const [statsResponse, ordersResponse] = await Promise.all([
        sellerAPI.getSellerStats(),
        sellerAPI.getSellerOrders({ limit: 5, sort: '-createdAt' })
      ]);

      const statsData = unwrapData(statsResponse);
      const ordersData = extractArray(ordersResponse);

      setStats(statsData && typeof statsData === 'object' ? statsData : {});
      setRecentOrders(ordersData.slice(0, 5));
    } catch (error) {
      console.error('Error fetching seller data:', error);
      setStats({});
      setRecentOrders([]);
    }

    await refreshProducts();
    setLoading(false);
  }, [extractArray, refreshProducts, unwrapData]);

  useEffect(() => {
    fetchDashboardData();
  }, [fetchDashboardData]);

  const alertsCount = products.filter(p => p.stock < 5).length;

  if (loading) return <LoadingSpinner message="Loading dashboard..." />;

  if (!user || user.role !== 'seller') {
    return <Message variant="danger">Access denied. Seller privileges required.</Message>;
  }

  // If we're on a sub-route, render the outlet
  if (location.pathname !== '/seller') {
    return <Outlet />;
  }

  return (
    <Container fluid className="py-4">
      <Row>
        <Col md={3} lg={2}>
          <Card>
            <Card.Header>
              <h6 className="mb-0">Seller Menu</h6>
            </Card.Header>
            <Card.Body className="p-0">
              <Nav variant="pills" className="flex-column">
                <Nav.Item>
                  <Nav.Link 
                    as={Link} 
                    to="/seller" 
                    active={location.pathname === '/seller'}
                  >
                    <i className="fas fa-tachometer-alt me-2"></i>
                    Dashboard
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link 
                    as={Link} 
                    to="/seller/products"
                    active={location.pathname === '/seller/products'}
                  >
                    <i className="fas fa-box me-2"></i>
                    Products
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link 
                    as={Link} 
                    to="/seller/orders"
                    active={location.pathname === '/seller/orders'}
                  >
                    <i className="fas fa-shopping-bag me-2"></i>
                    Orders
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link 
                    as={Link} 
                    to="/seller/analytics"
                    active={location.pathname === '/seller/analytics'}
                  >
                    <i className="fas fa-chart-bar me-2"></i>
                    Analytics
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link 
                    as={Link} 
                    to="/seller/profile"
                    active={location.pathname === '/seller/profile'}
                  >
                    <i className="fas fa-store me-2"></i>
                    Store Profile
                  </Nav.Link>
                </Nav.Item>

                {/* Add this navigation item for Inventory Alerts */}
                <Nav.Item>
                  <Nav.Link 
                    as={Link} 
                    to="/seller/alerts"
                    active={location.pathname === '/seller/alerts'}
                  >
                    <i className="fas fa-bell me-2"></i>
                    Inventory Alerts
                    {alertsCount > 0 && (
                      <Badge bg="danger" className="ms-2">
                        {alertsCount}
                      </Badge>
                    )}
                  </Nav.Link>
                </Nav.Item>
              </Nav>
            </Card.Body>
          </Card>
        </Col>

        <Col md={9} lg={10}>
          {/* Welcome Header */}
          <Card className="bg-primary text-white mb-4">
            <Card.Body>
              <Row className="align-items-center">
                <Col>
                  <h4 className="mb-1">Welcome back, {user.name}!</h4>
                  <p className="mb-0">Here's what's happening with your store today.</p>
                </Col>
                <Col xs="auto">
                  <Button variant="light" onClick={() => navigate('/seller/products/add')}>
                    <i className="fas fa-plus me-2"></i>
                    Add New Product
                  </Button>
                </Col>
              </Row>
            </Card.Body>
          </Card>

          {/* Stats Cards */}
          <Row className="mb-4">
            <Col xl={2} md={4} sm={6}>
              <Card className="border-primary">
                <Card.Body className="text-center">
                  <h4 className="text-primary">{formatPrice(stats.totalRevenue || 0)}</h4>
                  <small className="text-muted">Total Revenue</small>
                </Card.Body>
              </Card>
            </Col>
            <Col xl={2} md={4} sm={6}>
              <Card className="border-success">
                <Card.Body className="text-center">
                  <h4 className="text-success">{stats.totalOrders || 0}</h4>
                  <small className="text-muted">Total Orders</small>
                </Card.Body>
              </Card>
            </Col>
            <Col xl={2} md={4} sm={6}>
              <Card className="border-warning">
                <Card.Body className="text-center">
                  <h4 className="text-warning">{stats.totalProducts || 0}</h4>
                  <small className="text-muted">Products</small>
                </Card.Body>
              </Card>
            </Col>
            <Col xl={2} md={4} sm={6}>
              <Card className="border-info">
                <Card.Body className="text-center">
                  <h4 className="text-info">{stats.pendingOrders || 0}</h4>
                  <small className="text-muted">Pending Orders</small>
                </Card.Body>
              </Card>
            </Col>
            <Col xl={2} md={4} sm={6}>
              <Card className="border-secondary">
                <Card.Body className="text-center">
                  <h4 className="text-secondary">{stats.averageRating || 0}</h4>
                  <small className="text-muted">Avg Rating</small>
                </Card.Body>
              </Card>
            </Col>
            <Col xl={2} md={4} sm={6}>
              <Card className="border-danger">
                <Card.Body className="text-center">
                  <h4 className="text-danger">
                    {alertsCount}
                  </h4>
                  <small className="text-muted">Low Stock</small>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* Recent Orders */}
          <Row>
            <Col lg={6} className="mb-4">
              <Card>
                <Card.Header className="d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">Recent Orders</h5>
                  <Link to="/seller/orders" className="btn btn-sm btn-primary">
                    View All
                  </Link>
                </Card.Header>
                <Card.Body className="p-0">
                  <Table responsive hover className="mb-0">
                    <thead className="bg-light">
                      <tr>
                        <th>Order #</th>
                        <th>Customer</th>
                        <th>Amount</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {recentOrders.map(order => (
                        <tr key={order._id}>
                          <td>
                            <Link to={`/seller/orders/${order._id}`} className="text-decoration-none">
                              {order.orderNumber}
                            </Link>
                          </td>
                          <td>{order.customer.name}</td>
                          <td>{formatPrice(order.totalAmount)}</td>
                          <td>
                            <Badge bg={
                              order.status === 'delivered' ? 'success' :
                              order.status === 'cancelled' ? 'danger' :
                              order.status === 'pending' ? 'warning' : 'primary'
                            }>
                              {order.status}
                            </Badge>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Col>

            {/* Product Overview */}
            <Col lg={6} className="mb-4">
              <Card>
                <Card.Header className="d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">Product Overview</h5>
                  <Link to="/seller/products" className="btn btn-sm btn-primary">
                    Manage
                  </Link>
                </Card.Header>
                <Card.Body className="p-0">
                  <Table responsive hover className="mb-0">
                    <thead className="bg-light">
                      <tr>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Stock</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {products.map(product => (
                        <tr key={product._id}>
                          <td>{product.name.en}</td>
                          <td>{formatPrice(product.price)}</td>
                          <td>
                            <Badge bg={product.stock < 5 ? 'warning' : 'success'}>
                              {product.stock}
                            </Badge>
                          </td>
                          <td>
                            <Badge bg={product.status === 'active' ? 'success' : 'secondary'}>
                              {product.status}
                            </Badge>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* Quick Actions */}
          <Row>
            <Col>
              <Card>
                <Card.Header>
                  <h5 className="mb-0">Quick Actions</h5>
                </Card.Header>
                <Card.Body>
                  <Row>
                    <Col md={3} className="text-center mb-3">
                      <Button variant="outline-primary" className="w-100 h-100 py-3" onClick={() => navigate('/seller/products/add')}>
                        <i className="fas fa-plus fa-2x mb-2 d-block"></i>
                        Add Product
                      </Button>
                    </Col>
                    <Col md={3} className="text-center mb-3">
                      <Button variant="outline-success" className="w-100 h-100 py-3">
                        <i className="fas fa-edit fa-2x mb-2 d-block"></i>
                        Update Inventory
                      </Button>
                    </Col>
                    <Col md={3} className="text-center mb-3">
                      <Button variant="outline-warning" className="w-100 h-100 py-3">
                        <i className="fas fa-tags fa-2x mb-2 d-block"></i>
                        Create Promotion
                      </Button>
                    </Col>
                    <Col md={3} className="text-center mb-3">
                      <Button variant="outline-info" className="w-100 h-100 py-3">
                        <i className="fas fa-chart-line fa-2x mb-2 d-block"></i>
                        View Reports
                      </Button>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default SellerDashboard;
