import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Form, Modal, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { productsAPI, categoriesAPI } from '../../services/api';
import { useApp } from '../../contexts/AppContext';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import Message from '../../components/common/Message';
import { useAuth } from '../../contexts/AuthContext';

const SellerProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: { en: '', am: '' },
    description: { en: '', am: '' },
    price: '',
    originalPrice: '',
    category: '',
    stock: '',
    brand: '',
    location: {
      region: 'Addis Ababa',
      city: ''
    },
    weight: {
      value: '',
      unit: 'g'
    },
    tags: '',
    shipping: {
      freeShipping: false,
      shippingCost: '0'
    }
  });

  const { ethiopianRegions, language, formatPrice } = useApp();
  const { user, loading: authLoading } = useAuth();

  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    if (authLoading) return;

    if (!user) {
      setLoading(false);
      setError('You need to sign in as a seller to manage products.');
      return;
    }

    if (user.role !== 'seller') {
      setLoading(false);
      setError('Access denied. Seller privileges required.');
      return;
    }

    fetchProducts(user._id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [filter, user, authLoading]);

  const fetchProducts = async (sellerId = user?._id) => {
    if (!sellerId) {
      return;
    }

    try {
      setLoading(true);
      setError('');

      const params = { limit: 100, seller: sellerId };
      const response = await productsAPI.getProducts(params);
      const allProducts = response?.data?.data || [];

      let filteredProducts = allProducts;

      if (filter !== 'all') {
        if (filter === 'low-stock') {
          filteredProducts = allProducts.filter(product => Number(product.stock) < 10 && Number(product.stock) > 0);
        } else if (filter === 'out-of-stock') {
          filteredProducts = allProducts.filter(product => Number(product.stock) === 0);
        } else if (filter === 'inactive') {
          filteredProducts = allProducts.filter(product => product.status === 'inactive' || product.status === 'out_of_stock');
        }
      }

      setProducts(filteredProducts);
    } catch (error) {
      setError(error?.response?.data?.message || 'Failed to load products');
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await categoriesAPI.getCategories();
      const payload = response?.data;
      const categoryList = Array.isArray(payload) ? payload : payload?.data || [];
      setCategories(categoryList);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const handleCreateClick = () => {
    setEditingProduct(null);
    setFormData({
      name: { en: '', am: '' },
      description: { en: '', am: '' },
      price: '',
      originalPrice: '',
      category: '',
      stock: '',
      brand: '',
      location: {
        region: 'Addis Ababa',
        city: ''
      },
      weight: {
        value: '',
        unit: 'g'
      },
      tags: '',
      shipping: {
        freeShipping: false,
        shippingCost: '0'
      }
    });
    setShowModal(true);
  };

  const handleEditClick = (product) => {
    setEditingProduct(product);
    setFormData({
      name: product.name,
      description: product.description,
      price: product.price,
      originalPrice: product.originalPrice || '',
      category: product.category?._id || '',
      stock: product.stock,
      brand: product.brand || '',
      location: product.location,
      weight: product.weight || { value: '', unit: 'g' },
      tags: product.tags?.join(', ') || '',
      shipping: product.shipping || { freeShipping: false, shippingCost: '0' }
    });
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const submitData = {
        ...formData,
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        originalPrice: formData.originalPrice ? parseFloat(formData.originalPrice) : undefined,
        shipping: {
          ...formData.shipping,
          shippingCost: parseFloat(formData.shipping.shippingCost)
        }
      };

      if (editingProduct) {
        await productsAPI.updateProduct(editingProduct._id, submitData);
      } else {
        await productsAPI.createProduct(submitData);
      }

      setShowModal(false);
      fetchProducts();
    } catch (error) {
      alert(`Failed to ${editingProduct ? 'update' : 'create'} product`);
    }
  };

  const handleDelete = async (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        await productsAPI.deleteProduct(productId);
        fetchProducts();
      } catch (error) {
        alert('Failed to delete product');
      }
    }
  };

  const handleStatusToggle = async (productId, currentStatus) => {
    try {
      const newStatus = currentStatus === 'active' ? 'inactive' : 'active';
      await productsAPI.updateProduct(productId, { status: newStatus });
      fetchProducts();
    } catch (error) {
      alert('Failed to update product status');
    }
  };

  const getStockVariant = (stock) => {
    if (stock === 0) return 'danger';
    if (stock < 10) return 'warning';
    return 'success';
  };

  const getStockText = (stock) => {
    if (stock === 0) return 'Out of Stock';
    if (stock < 10) return `Low (${stock})`;
    return `In Stock (${stock})`;
  };

  if (authLoading || (loading && !error)) {
    return <LoadingSpinner message="Loading your products..." />;
  }

  if (!user || user.role !== 'seller') {
    return <Message variant="danger">{error || 'Access denied. Seller privileges required.'}</Message>;
  }

  return (
    <Container fluid>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h1>My Products</h1>
          <p className="text-muted mb-0">Manage your product catalog</p>
        </div>
        <div className="d-flex gap-2">
          <Form.Select 
            style={{ width: '200px' }}
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          >
            <option value="all">All Products</option>
            <option value="low-stock">Low Stock</option>
            <option value="out-of-stock">Out of Stock</option>
            <option value="inactive">Inactive</option>
          </Form.Select>
          <Button variant="primary" onClick={handleCreateClick}>
            <i className="fas fa-plus me-2"></i>
            Add Product
          </Button>
        </div>
      </div>

      {error && <Alert variant="danger">{error}</Alert>}

      {/* Quick Stats */}
      <Row className="mb-4">
        <Col xl={2} md={4} sm={6}>
          <Card className="border-primary">
            <Card.Body className="text-center">
              <h4 className="text-primary">{products.length}</h4>
              <small className="text-muted">Total Products</small>
            </Card.Body>
          </Card>
        </Col>
        <Col xl={2} md={4} sm={6}>
          <Card className="border-success">
            <Card.Body className="text-center">
              <h4 className="text-success">
                {products.filter(p => p.stock > 0).length}
              </h4>
              <small className="text-muted">In Stock</small>
            </Card.Body>
          </Card>
        </Col>
        <Col xl={2} md={4} sm={6}>
          <Card className="border-warning">
            <Card.Body className="text-center">
              <h4 className="text-warning">
                {products.filter(p => p.stock < 10 && p.stock > 0).length}
              </h4>
              <small className="text-muted">Low Stock</small>
            </Card.Body>
          </Card>
        </Col>
        <Col xl={2} md={4} sm={6}>
          <Card className="border-danger">
            <Card.Body className="text-center">
              <h4 className="text-danger">
                {products.filter(p => p.stock === 0).length}
              </h4>
              <small className="text-muted">Out of Stock</small>
            </Card.Body>
          </Card>
        </Col>
        <Col xl={2} md={4} sm={6}>
          <Card className="border-info">
            <Card.Body className="text-center">
              <h4 className="text-info">
                {products.filter(p => p.status === 'active').length}
              </h4>
              <small className="text-muted">Active</small>
            </Card.Body>
          </Card>
        </Col>
        <Col xl={2} md={4} sm={6}>
          <Card className="border-secondary">
            <Card.Body className="text-center">
              <h4 className="text-secondary">
                {products.filter(p => p.status === 'inactive').length}
              </h4>
              <small className="text-muted">Inactive</small>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Card>
        <Card.Header>
          <h5 className="mb-0">
            Products ({products.length})
            {filter !== 'all' && <span className="text-muted"> - {filter.replace('-', ' ')}</span>}
          </h5>
        </Card.Header>
        <Card.Body className="p-0">
          <Table responsive hover className="mb-0">
            <thead className="bg-light">
              <tr>
                <th>Product</th>
                <th>Category</th>
                <th>Price</th>
                <th>Stock</th>
                <th>Status</th>
                <th>Sales</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product._id}>
                  <td>
                    <div className="d-flex align-items-center">
                      <img 
                        src={product.images[0]?.url || '/images/placeholder.jpg'} 
                        alt={product.name.en}
                        className="rounded me-3"
                        style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                      />
                      <div>
                        <div className="fw-semibold">{product.name.en}</div>
                        {product.name.am && (
                          <small className="text-muted">{product.name.am}</small>
                        )}
                        <div>
                          <small className="text-muted">SKU: {product.sku}</small>
                        </div>
                      </div>
                    </div>
                  </td>
                  <td>
                    <Badge bg="secondary">
                      {product.category?.name?.[language] || product.category?.name?.en}
                    </Badge>
                  </td>
                  <td>
                    <strong>{formatPrice(product.price)}</strong>
                    {product.originalPrice && product.originalPrice > product.price && (
                      <div>
                        <small className="text-muted text-decoration-line-through">
                          {formatPrice(product.originalPrice)}
                        </small>
                      </div>
                    )}
                  </td>
                  <td>
                    <Badge bg={getStockVariant(product.stock)}>
                      {getStockText(product.stock)}
                    </Badge>
                  </td>
                  <td>
                    <Badge bg={product.status === 'active' ? 'success' : 'secondary'}>
                      {product.status}
                    </Badge>
                  </td>
                  <td>
                    <Badge bg="info">0</Badge>
                  </td>
                  <td>
                    <div className="d-flex gap-1">
                      <Button
                        as={Link}
                        to={`/product/${product._id}`}
                        variant="outline-primary"
                        size="sm"
                        target="_blank"
                      >
                        View
                      </Button>
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => handleEditClick(product)}
                      >
                        Edit
                      </Button>
                      <Button
                        variant={product.status === 'active' ? 'warning' : 'success'}
                        size="sm"
                        onClick={() => handleStatusToggle(product._id, product.status)}
                      >
                        {product.status === 'active' ? 'Deactivate' : 'Activate'}
                      </Button>
                      <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() => handleDelete(product._id)}
                      >
                        Delete
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

          {products.length === 0 && (
            <div className="text-center py-5">
              <i className="fas fa-box-open fa-3x text-muted mb-3"></i>
              <h5>No products found</h5>
              <p className="text-muted">
                {filter === 'all' ? 'You haven\'t added any products yet.' : `No ${filter.replace('-', ' ')} products found.`}
              </p>
              <Button variant="primary" onClick={handleCreateClick}>
                Add Your First Product
              </Button>
            </div>
          )}
        </Card.Body>
      </Card>

      {/* Product Form Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            {editingProduct ? 'Edit Product' : 'Add New Product'}
          </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>English Name *</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.name.en}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      name: { ...prev.name, en: e.target.value }
                    }))}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Amharic Name *</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.name.am}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      name: { ...prev.name, am: e.target.value }
                    }))}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>English Description *</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={formData.description.en}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      description: { ...prev.description, en: e.target.value }
                    }))}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Amharic Description *</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={formData.description.am}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      description: { ...prev.description, am: e.target.value }
                    }))}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Price (ETB) *</Form.Label>
                  <Form.Control
                    type="number"
                    step="0.01"
                    value={formData.price}
                    onChange={(e) => setFormData(prev => ({ ...prev, price: e.target.value }))}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Original Price (ETB)</Form.Label>
                  <Form.Control
                    type="number"
                    step="0.01"
                    value={formData.originalPrice}
                    onChange={(e) => setFormData(prev => ({ ...prev, originalPrice: e.target.value }))}
                    placeholder="For display discount"
                  />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Stock *</Form.Label>
                  <Form.Control
                    type="number"
                    value={formData.stock}
                    onChange={(e) => setFormData(prev => ({ ...prev, stock: e.target.value }))}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Category *</Form.Label>
                  <Form.Select
                    value={formData.category}
                    onChange={(e) => setFormData(prev => ({ ...prev, category: e.target.value }))}
                    required
                  >
                    <option value="">Select Category</option>
                    {categories.map(category => (
                      <option key={category._id} value={category._id}>
                        {category.name.en}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Brand</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.brand}
                    onChange={(e) => setFormData(prev => ({ ...prev, brand: e.target.value }))}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Region *</Form.Label>
                  <Form.Select
                    value={formData.location.region}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      location: { ...prev.location, region: e.target.value }
                    }))}
                    required
                  >
                    {ethiopianRegions.map(region => (
                      <option key={region} value={region}>
                        {region}
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>City *</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.location.city}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      location: { ...prev.location, city: e.target.value }
                    }))}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Tags</Form.Label>
              <Form.Control
                type="text"
                value={formData.tags}
                onChange={(e) => setFormData(prev => ({ ...prev, tags: e.target.value }))}
                placeholder="Separate tags with commas (e.g., electronics, gadget, mobile)"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                type="checkbox"
                label="Free Shipping"
                checked={formData.shipping.freeShipping}
                onChange={(e) => setFormData(prev => ({
                  ...prev,
                  shipping: { ...prev.shipping, freeShipping: e.target.checked }
                }))}
              />
            </Form.Group>

            {!formData.shipping.freeShipping && (
              <Form.Group className="mb-3">
                <Form.Label>Shipping Cost (ETB)</Form.Label>
                <Form.Control
                  type="number"
                  step="0.01"
                  value={formData.shipping.shippingCost}
                  onChange={(e) => setFormData(prev => ({
                    ...prev,
                    shipping: { ...prev.shipping, shippingCost: e.target.value }
                  }))}
                />
              </Form.Group>
            )}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowModal(false)}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              {editingProduct ? 'Update Product' : 'Add Product'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </Container>
  );
};

export default SellerProductsPage;
