import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Form, Modal, ListGroup, ProgressBar } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { ordersAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';
import LoadingSpinner from '../components/common/LoadingSpinner';
import Message from '../components/common/Message';
import '../styles/ordersPage.css';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [trackingOrder, setTrackingOrder] = useState(null);
  const [showTrackingModal, setShowTrackingModal] = useState(false);
  const [copyFeedback, setCopyFeedback] = useState('');

  const { formatPrice } = useApp();

  useEffect(() => {
    fetchOrders();
  }, []);

  useEffect(() => {
    if (!copyFeedback) return undefined;
    const timeout = setTimeout(() => setCopyFeedback(''), 2500);
    return () => clearTimeout(timeout);
  }, [copyFeedback]);

  const fetchOrders = async () => {
    try {
      const response = await ordersAPI.getOrders();
      setOrders(response.data.data);
    } catch (error) {
      setError('Failed to load orders');
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusVariant = (status) => {
    const variants = {
      pending: 'warning',
      confirmed: 'info',
      processing: 'primary',
      shipped: 'secondary',
      delivered: 'success',
      cancelled: 'danger',
      returned: 'dark'
    };
    return variants[status] || 'secondary';
  };

  const getPaymentStatusVariant = (status) => {
    const variants = {
      pending: 'warning',
      processing: 'info',
      completed: 'success',
      failed: 'danger',
      refunded: 'dark',
      cancelled: 'secondary'
    };
    return variants[status] || 'secondary';
  };

  const getStatusIcon = (status) => {
    const icons = {
      pending: 'fas fa-hourglass-half',
      confirmed: 'fas fa-check-circle',
      processing: 'fas fa-cogs',
      shipped: 'fas fa-shipping-fast',
      delivered: 'fas fa-box-open',
      cancelled: 'fas fa-times-circle',
      returned: 'fas fa-undo'
    };
    return icons[status] || 'fas fa-circle';
  };

  const getPaymentIcon = (method) => {
    const map = {
      telebirr: 'fas fa-mobile-alt',
      cbe: 'fas fa-university',
      cod: 'fas fa-hand-holding-usd',
      bank_transfer: 'fas fa-exchange-alt',
      chapa: 'fas fa-credit-card'
    };
    return map[method] || 'fas fa-wallet';
  };

  const filteredOrders = orders.filter(order => {
    if (filter === 'all') return true;
    return order.status === filter;
  });

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatDateOnly = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const openTrackingModal = (order) => {
    setTrackingOrder(order);
    setShowTrackingModal(true);
    setCopyFeedback('');
  };

  const closeTrackingModal = () => {
    setShowTrackingModal(false);
    setTrackingOrder(null);
    setCopyFeedback('');
  };

  const handleCopyTrackingNumber = async (trackingNumber) => {
    if (!trackingNumber) return;

    try {
      if (typeof navigator !== 'undefined' && navigator.clipboard && navigator.clipboard.writeText) {
        await navigator.clipboard.writeText(trackingNumber);
        setCopyFeedback('Tracking number copied!');
      } else {
        throw new Error('Clipboard API not available');
      }
    } catch (err) {
      console.warn('Clipboard copy failed, falling back to prompt.', err);
      window.prompt('Copy the tracking number:', trackingNumber);
      setCopyFeedback('Copied manually.');
    }
  };

  const buildTrackingDetails = (order) => {
    if (!order) {
      return { steps: [], currentIndex: -1 };
    }

    const steps = [
      {
        key: 'pending',
        label: 'Order Placed',
        description: 'We received your order and sent a confirmation email.'
      },
      {
        key: 'confirmed',
        label: 'Order Confirmed',
        description: 'A seller confirmed item availability.'
      },
      {
        key: 'processing',
        label: 'Preparing Order',
        description: 'Items are being packaged for shipment.'
      },
      {
        key: 'shipped',
        label: 'Shipped',
        description: 'Your package is on the way to the delivery address.'
      },
      {
        key: 'delivered',
        label: 'Delivered',
        description: 'The package arrived at its destination.'
      }
    ];

    if (order.status === 'cancelled') {
      steps.push({
        key: 'cancelled',
        label: 'Order Cancelled',
        description: order.cancellationReason || 'The order was cancelled.',
        variant: 'danger'
      });
    } else if (order.status === 'returned') {
      steps.push({
        key: 'returned',
        label: 'Return Completed',
        description: order.returnReason || 'The order was returned and processed.',
        variant: 'warning'
      });
    }

    const currentIndex = steps.findIndex(step => step.key === order.status);
    const effectiveIndex = currentIndex >= 0 ? currentIndex : steps.findIndex(step => step.key === 'delivered');

    const decoratedSteps = steps.map((step, index) => {
      let state = 'upcoming';
      if (effectiveIndex < 0) {
        state = index === 0 ? 'current' : 'upcoming';
      } else if (index < effectiveIndex) {
        state = 'completed';
      } else if (index === effectiveIndex) {
        state = order.status === 'cancelled' ? 'cancelled' : order.status === 'returned' ? 'returned' : 'current';
      }
      return {
        ...step,
        state
      };
    });

    return { steps: decoratedSteps, currentIndex: effectiveIndex };
  };

  const renderStepIcon = (step) => {
    if (step.state === 'cancelled') {
      return <i className="fas fa-ban text-danger me-2"></i>;
    }
    if (step.state === 'returned') {
      return <i className="fas fa-undo text-warning me-2"></i>;
    }
    if (step.state === 'completed') {
      return <i className="fas fa-check-circle text-success me-2"></i>;
    }
    if (step.state === 'current') {
      return <i className="fas fa-dot-circle text-primary me-2"></i>;
    }
    return <i className="far fa-circle text-muted me-2"></i>;
  };

  if (loading) return <LoadingSpinner message="Loading your orders..." />;

  return (
    <div className="orders-page-wrapper py-5">
      <Container>
      <Row>
        <Col>
          <div className="orders-hero mb-5">
            <div>
              <h1 className="orders-title">My Orders</h1>
              <p className="orders-subtitle">Track purchases, shipping updates, and delivery milestones in one beautiful place.</p>
            </div>
            <Form.Select 
              className="orders-filter"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
            >
              <option value="all">All Orders</option>
              <option value="pending">Pending</option>
              <option value="confirmed">Confirmed</option>
              <option value="processing">Processing</option>
              <option value="shipped">Shipped</option>
              <option value="delivered">Delivered</option>
              <option value="cancelled">Cancelled</option>
            </Form.Select>
          </div>

          {error && <Message variant="danger">{error}</Message>}

          {filteredOrders.length === 0 ? (
            <Message variant="info">
              <div className="orders-empty text-center py-5">
                <div className="orders-empty-icon mb-3">
                  <i className="fas fa-box-open"></i>
                </div>
                <h4 className="mb-2">No orders found</h4>
                <p className="text-muted mb-4">
                  {filter === 'all' 
                    ? "You haven't placed any orders yet." 
                    : `No ${filter} orders found.`}
                </p>
                <Button as={Link} to="/products" variant="primary" className="orders-cta-button">
                  Start Shopping
                </Button>
              </div>
            </Message>
          ) : (
            <Card className="orders-card shadow-sm">
              <Card.Body className="p-0">
                <Table responsive hover className="orders-table mb-0">
                  <thead>
                    <tr>
                      <th>Order #</th>
                      <th>Date</th>
                      <th>Items</th>
                      <th>Total</th>
                      <th>Status</th>
                      <th>Payment</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredOrders.map(order => (
                      <tr key={order._id}>
                        <td>
                          <div className="orders-table-order-number">{order.orderNumber}</div>
                          <small className="text-muted">{order.items.length} {order.items.length === 1 ? 'item' : 'items'}</small>
                        </td>
                        <td>
                          <small>{formatDate(order.createdAt)}</small>
                        </td>
                        <td>
                          <div className="orders-item-preview">
                            {order.items.slice(0, 2).map((item, index) => (
                              <div key={index} className="d-flex align-items-center mb-1">
                                <img 
                                  src={item.image} 
                                  alt={item.name.en}
                                  className="rounded me-2"
                                  style={{ width: '30px', height: '30px', objectFit: 'cover' }}
                                />
                                <small className="text-truncate" style={{ maxWidth: '150px' }}>
                                  {item.name.en}
                                </small>
                                {item.quantity > 1 && (
                                  <small className="text-muted ms-1">x{item.quantity}</small>
                                )}
                              </div>
                            ))}
                            {order.items.length > 2 && (
                              <small className="text-muted">
                                +{order.items.length - 2} more items
                              </small>
                            )}
                          </div>
                        </td>
                        <td>
                          <strong>{formatPrice(order.totalAmount)}</strong>
                        </td>
                        <td>
                          <Badge bg={getStatusVariant(order.status)} className="orders-status-badge">
                            <i className={`${getStatusIcon(order.status)} me-2`}></i>
                            {order.status.replace('_', ' ').toUpperCase()}
                          </Badge>
                          {order.trackingNumber && (
                            <div className="orders-tracking-inline">
                              <small className="text-muted">Tracking #{order.trackingNumber}</small>
                            </div>
                          )}
                        </td>
                        <td>
                          <Badge bg={getPaymentStatusVariant(order.paymentStatus)} className="orders-status-badge">
                            {order.paymentStatus.replace('_', ' ').toUpperCase()}
                          </Badge>
                          <div>
                            <small className="text-muted text-capitalize">
                              <i className={`${getPaymentIcon(order.paymentMethod)} me-2`}></i>
                              {order.paymentMethod?.replace('_', ' ')}
                            </small>
                          </div>
                        </td>
                        <td>
                          <div className="d-flex flex-wrap gap-2">
                            <Button
                              as={Link}
                              to={`/order/${order._id}`}
                              variant="outline-primary"
                              size="sm"
                              className="orders-action-btn"
                            >
                              <i className="fas fa-search me-2"></i>
                              View
                            </Button>
                            {order.trackingNumber && (
                              <Button
                                variant="outline-secondary"
                                size="sm"
                                className="orders-action-btn"
                                onClick={() => openTrackingModal(order)}
                              >
                                <i className="fas fa-location-arrow me-2"></i>
                                Track Package
                              </Button>
                            )}
                            {order.status === 'pending' && (
                              <Button
                                variant="outline-danger"
                                size="sm"
                                className="orders-action-btn"
                                onClick={async () => {
                                  if (window.confirm('Are you sure you want to cancel this order?')) {
                                    try {
                                      await ordersAPI.cancelOrder(order._id, 'Cancelled by customer');
                                      fetchOrders(); // Refresh orders
                                    } catch (error) {
                                      alert('Failed to cancel order');
                                    }
                                  }
                                }}
                              >
                                Cancel
                              </Button>
                            )}
                            {order.status === 'delivered' && (
                              <Button
                                variant="outline-success"
                                size="sm"
                                className="orders-action-btn"
                                onClick={() => {
                                  // Implement reorder functionality
                                  alert('Reorder functionality coming soon!');
                                }}
                              >
                                <i className="fas fa-redo me-2"></i>
                                Reorder
                              </Button>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          )}

          {/* Order Statistics */}
          {orders.length > 0 && (
            <Row className="mt-5 g-3 orders-stats-row">
              <Col md={3} sm={6}>
                <Card className="orders-stat-card shadow-sm">
                  <Card.Body>
                    <div className="orders-stat-icon bg-primary text-white">
                      <i className="fas fa-shopping-basket"></i>
                    </div>
                    <h3>{orders.length}</h3>
                    <p className="text-muted mb-0">Total Orders</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3} sm={6}>
                <Card className="orders-stat-card shadow-sm">
                  <Card.Body>
                    <div className="orders-stat-icon bg-success text-white">
                      <i className="fas fa-box"></i>
                    </div>
                    <h3 className="text-success">
                      {orders.filter(o => o.status === 'delivered').length}
                    </h3>
                    <p className="text-muted mb-0">Delivered</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3} sm={6}>
                <Card className="orders-stat-card shadow-sm">
                  <Card.Body>
                    <div className="orders-stat-icon bg-warning text-dark">
                      <i className="fas fa-spinner"></i>
                    </div>
                    <h3 className="text-warning">
                      {orders.filter(o => ['pending', 'confirmed', 'processing'].includes(o.status)).length}
                    </h3>
                    <p className="text-muted mb-0">In Progress</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3} sm={6}>
                <Card className="orders-stat-card shadow-sm">
                  <Card.Body>
                    <div className="orders-stat-icon bg-danger text-white">
                      <i className="fas fa-times"></i>
                    </div>
                    <h3 className="text-danger">
                      {orders.filter(o => o.status === 'cancelled').length}
                    </h3>
                    <p className="text-muted mb-0">Cancelled</p>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          )}
        </Col>
      </Row>

      <Modal show={showTrackingModal} onHide={closeTrackingModal} centered size="lg">
        <Modal.Header closeButton className="orders-tracking-header">
          <Modal.Title>Track Shipment</Modal.Title>
        </Modal.Header>
        <Modal.Body className="orders-tracking-body">
          {trackingOrder && (
            <>
              <Row className="mb-3">
                <Col md={6} className="mb-3">
                  <p className="text-muted mb-1">Tracking Number</p>
                  <div className="d-flex align-items-center gap-2 flex-wrap">
                    <h5 className="mb-0">{trackingOrder.trackingNumber}</h5>
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      onClick={() => handleCopyTrackingNumber(trackingOrder.trackingNumber)}
                    >
                      Copy
                    </Button>
                    {copyFeedback && (
                      <small className="text-success">{copyFeedback}</small>
                    )}
                  </div>
                </Col>
                <Col md={3} className="mb-3">
                  <p className="text-muted mb-1">Carrier</p>
                  <div>{trackingOrder.carrier || 'Not provided'}</div>
                </Col>
                <Col md={3} className="mb-3">
                  <p className="text-muted mb-1">Shipping Method</p>
                  <div className="text-capitalize">{trackingOrder.shippingMethod || 'Standard'}</div>
                </Col>
              </Row>

              <Row className="mb-3">
                <Col md={4} className="mb-3">
                  <p className="text-muted mb-1">Order Status</p>
                  <Badge bg={getStatusVariant(trackingOrder.status)}>
                    {trackingOrder.status.replace('_', ' ').toUpperCase()}
                  </Badge>
                </Col>
                <Col md={4} className="mb-3">
                  <p className="text-muted mb-1">Placed On</p>
                  <div>{formatDateOnly(trackingOrder.createdAt)}</div>
                </Col>
                <Col md={4} className="mb-3">
                  <p className="text-muted mb-1">Estimated Delivery</p>
                  <div>
                    {trackingOrder.estimatedDelivery
                      ? formatDateOnly(trackingOrder.estimatedDelivery)
                      : 'Pending update'}
                  </div>
                </Col>
              </Row>

              {(() => {
                const { steps, currentIndex } = buildTrackingDetails(trackingOrder);
                const progressPercent = steps.length
                  ? Math.min(100, Math.round(((currentIndex >= 0 ? currentIndex + 1 : 1) / steps.length) * 100))
                  : 0;

                return (
                  <>
                    <div className="mb-3">
                      <ProgressBar now={progressPercent} label={`${progressPercent}%`} visuallyHidden={progressPercent === 0} />
                      {progressPercent === 0 && (
                        <small className="text-muted">Tracking updates will appear once your order is processed.</small>
                      )}
                    </div>
                    <ListGroup variant="flush" className="orders-tracking-steps">
                      {steps.map(step => (
                        <ListGroup.Item key={step.key} className="d-flex align-items-start">
                          <div className="me-2 mt-1">
                            {renderStepIcon(step)}
                          </div>
                          <div>
                            <div className="fw-semibold d-flex align-items-center gap-2">
                              {step.label}
                              {step.state === 'cancelled' && (
                                <Badge bg="danger">Cancelled</Badge>
                              )}
                              {step.state === 'returned' && (
                                <Badge bg="warning" text="dark">Returned</Badge>
                              )}
                              {step.state === 'current' && step.key !== 'delivered' && step.key !== 'shipped' && (
                                <Badge bg="primary">In Progress</Badge>
                              )}
                            </div>
                            <small className="text-muted">{step.description}</small>
                          </div>
                        </ListGroup.Item>
                      ))}
                    </ListGroup>
                  </>
                );
              })()}
            </>
          )}
        </Modal.Body>
        <Modal.Footer className="orders-tracking-footer">
          <Button variant="secondary" onClick={closeTrackingModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
      </Container>
    </div>
  );
};

export default OrdersPage;
