import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Form, Button, Alert, Badge } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import { useApp } from '../contexts/AppContext';
import { ordersAPI, promotionsAPI } from '../services/api';
import CheckoutSteps from '../components/checkout/CheckoutSteps';
import PaymentMethod from '../components/checkout/PaymentMethod';
import Message from '../components/common/Message';
import '../styles/checkoutPage.css';

const CheckoutPage = () => {
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  // Form states
  const [shippingAddress, setShippingAddress] = useState({
    name: '',
    phone: '',
    region: 'Addis Ababa',
    city: '',
    subcity: '',
    woreda: '',
    kebele: '',
    houseNumber: '',
    specificLocation: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');

  const { cartItems, getCartSummary, resolveImageForCartItem } = useCart();
  const { user } = useAuth();
  const { ethiopianRegions, formatPrice } = useApp();
  const navigate = useNavigate();

  const summary = getCartSummary();

  const heroHighlights = [
    {
      icon: 'fas fa-shipping-fast',
      title: 'Nationwide delivery',
      subtitle: 'On average 2-4 business days'
    },
    {
      icon: 'fas fa-wallet',
      title: 'Flexible payments',
      subtitle: 'TeleBirr · CBE · Chapa · COD'
    },
    {
      icon: 'fas fa-headset',
      title: 'Dedicated support',
      subtitle: 'Live chat & hotline 24/7'
    }
  ];

  const supportHighlights = [
    'Need help finalizing your order? Call +251 987 654 321',
    'Secure checkout with encryption across all methods',
    'Returns accepted within 7 days for eligible products'
  ];

  const checkoutBenefits = [
    {
      icon: 'fas fa-boxes',
      title: 'Order summary snapshots',
      text: 'Visual overview of products, quantities, and savings in one place.'
    },
    {
      icon: 'fas fa-gift',
      title: 'Apply promo magic',
      text: 'Use seasonal codes for discounts and automatic free shipping eligibility.'
    },
    {
      icon: 'fas fa-biking',
      title: 'Express Addis dispatch',
      text: 'Same-day options for Addis Ababa when you complete checkout before 2 PM.'
    }
  ];

  // Promotion code states
  const [promotionCode, setPromotionCode] = useState('');
  const [appliedPromotion, setAppliedPromotion] = useState(null);
  const [promotionDiscount, setPromotionDiscount] = useState(0);
  const [promotionError, setPromotionError] = useState('');
  const [applyingPromotion, setApplyingPromotion] = useState(false);

  // Initialize shipping address with user data
  useEffect(() => {
    if (user) {
      setShippingAddress(prev => ({
        ...prev,
        name: user.name,
        phone: user.phone,
        region: user.address?.region || 'Addis Ababa',
        city: user.address?.city || '',
        subcity: user.address?.subcity || '',
        woreda: user.address?.woreda || '',
        kebele: user.address?.kebele || '',
        houseNumber: user.address?.houseNumber || '',
        specificLocation: user.address?.specificLocation || ''
      }));
    }
  }, [user]);

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setShippingAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleContinueToPayment = () => {
    // Validate shipping address
    if (!shippingAddress.name || !shippingAddress.phone || !shippingAddress.city || !shippingAddress.specificLocation) {
      setError('Please fill in all required address fields');
      return;
    }
    setStep(2);
    setError('');
  };

  const handlePlaceOrder = async () => {
    if (!paymentMethod) {
      setError('Please select a payment method');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Create order
      const orderData = {
        items: cartItems.map(item => ({
          product: item._id,
          quantity: item.quantity,
          image: resolveImageForCartItem?.(item)
        })),
        shippingAddress,
        paymentMethod,
        customerNotes,
        promotion: appliedPromotion?._id || null
      };

      const orderResponse = await ordersAPI.createOrder(orderData);
      const newOrder = orderResponse.data.data;

      navigate(`/order/${newOrder._id}/payment`, {
        state: {
          order: newOrder,
          paymentMethod,
          telebirrPhone: shippingAddress.phone
        }
      });

    } catch (error) {
      setError(error.response?.data?.message || 'Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  // Promotion functions
  const applyPromotion = async () => {
    if (!promotionCode.trim()) return;

    setApplyingPromotion(true);
    setPromotionError('');

    try {
      const response = await promotionsAPI.validatePromotion({
        code: promotionCode,
        cartItems: cartItems.map(item => ({
          product: {
            _id: item._id,
            category: item.category,
            seller: item.seller
          },
          price: item.price,
          quantity: item.quantity
        })),
        totalAmount: summary.subtotal,
        userId: user._id
      });

      setAppliedPromotion(response.data.data.promotion);
      setPromotionDiscount(response.data.data.discount);
    } catch (error) {
      setPromotionError(error.response?.data?.message || 'Failed to apply promotion');
    } finally {
      setApplyingPromotion(false);
    }
  };

  const removePromotion = () => {
    setPromotionCode('');
    setAppliedPromotion(null);
    setPromotionDiscount(0);
    setPromotionError('');
  };

  const finalTotal = summary.total - promotionDiscount;

  if (cartItems.length === 0) {
    return (
      <Container className="py-5">
  <Message variant="info">
          <h4>Your cart is empty</h4>
          <p>Add some products to your cart before checking out.</p>
          <Button variant="primary" onClick={() => navigate('/products')}>
            Continue Shopping
          </Button>
        </Message>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <CheckoutSteps 
        step1={true} 
        step2={step >= 2} 
        step3={step >= 3} 
        step4={step >= 4} 
      />

      {error && <Alert variant="danger">{error}</Alert>}

      <Row>
        {/* Checkout Steps */}
        <Col lg={8}>
          {step === 1 && (
            <Card>
              <Card.Header>
                <h4 className="mb-0">Shipping Address</h4>
              </Card.Header>
              <Card.Body>
                <Form>
                  <Row>
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Full Name *</Form.Label>
                        <Form.Control
                          type="text"
                          name="name"
                          value={shippingAddress.name}
                          onChange={handleAddressChange}
                          required
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Phone Number *</Form.Label>
                        <Form.Control
                          type="tel"
                          name="phone"
                          value={shippingAddress.phone}
                          onChange={handleAddressChange}
                          required
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row>
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Region *</Form.Label>
                        <Form.Select
                          name="region"
                          value={shippingAddress.region}
                          onChange={handleAddressChange}
                          required
                        >
                          {ethiopianRegions.map(region => (
                            <option key={region} value={region}>
                              {region}
                            </option>
                          ))}
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>City *</Form.Label>
                        <Form.Control
                          type="text"
                          name="city"
                          value={shippingAddress.city}
                          onChange={handleAddressChange}
                          required
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row>
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Subcity</Form.Label>
                        <Form.Control
                          type="text"
                          name="subcity"
                          value={shippingAddress.subcity}
                          onChange={handleAddressChange}
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Woreda</Form.Label>
                        <Form.Control
                          type="text"
                          name="woreda"
                          value={shippingAddress.woreda}
                          onChange={handleAddressChange}
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Form.Group className="mb-3">
                    <Form.Label>Specific Location *</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={3}
                      name="specificLocation"
                      value={shippingAddress.specificLocation}
                      onChange={handleAddressChange}
                      required
                      placeholder="Detailed location description with landmarks"
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Order Notes (Optional)</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={2}
                      value={customerNotes}
                      onChange={(e) => setCustomerNotes(e.target.value)}
                      placeholder="Any special instructions for your order..."
                    />
                  </Form.Group>

                  <div className="d-flex justify-content-between">
                    <Button variant="outline-secondary" onClick={() => navigate('/cart')}>
                      Back to Cart
                    </Button>
                    <Button variant="primary" onClick={handleContinueToPayment}>
                      Continue to Payment
                    </Button>
                  </div>
                </Form>
              </Card.Body>
            </Card>
          )}

          {step === 2 && (
            <Card>
              <Card.Header>
                <h4 className="mb-0">Payment Method</h4>
              </Card.Header>
              <Card.Body>
                <PaymentMethod 
                  onMethodSelect={setPaymentMethod}
                  selectedMethod={paymentMethod}
                />
                
                <div className="d-flex justify-content-between mt-4">
                  <Button variant="outline-secondary" onClick={() => setStep(1)}>
                    Back to Shipping
                  </Button>
                  <Button 
                    variant="primary" 
                    onClick={handlePlaceOrder}
                    disabled={!paymentMethod || loading}
                  >
                    {loading ? 'Placing Order...' : 'Place Order'}
                  </Button>
                </div>
              </Card.Body>
            </Card>
          )}
        </Col>

        {/* Order Summary */}
        <Col lg={4}>
          <Card className="sticky-top" style={{ top: '20px' }}>
            <Card.Header>
              <h5 className="mb-0">Order Summary</h5>
            </Card.Header>
            <Card.Body>
              {cartItems.map(item => (
                <div key={item._id} className="d-flex justify-content-between align-items-center mb-2">
                  <div className="d-flex align-items-center gap-2">
                    <img
                      src={resolveImageForCartItem?.(item)}
                      alt={item.name?.en || item.name}
                      style={{ width: '45px', height: '45px', objectFit: 'cover', borderRadius: '6px' }}
                    />
                    <div>
                      <small className="d-block">{item.name?.en || item.name}</small>
                      <small className="text-muted">Qty: {item.quantity}</small>
                    </div>
                  </div>
                  <small>{formatPrice(item.price * item.quantity)}</small>
                </div>
              ))}

              <hr />

              {/* Promotion Code */}
              <Card className="mb-4">
                <Card.Header>
                  <h5 className="mb-0">Promotion Code</h5>
                </Card.Header>
                <Card.Body>
                  {!appliedPromotion ? (
                    <div className="d-flex gap-2">
                      <Form.Control
                        type="text"
                        value={promotionCode}
                        onChange={(e) => setPromotionCode(e.target.value.toUpperCase())}
                        placeholder="Enter promo code"
                        disabled={applyingPromotion}
                      />
                      <Button 
                        variant="outline-primary" 
                        onClick={applyPromotion}
                        disabled={applyingPromotion || !promotionCode.trim()}
                      >
                        {applyingPromotion ? 'Applying...' : 'Apply'}
                      </Button>
                    </div>
                  ) : (
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <Badge bg="success" className="me-2">{appliedPromotion.code}</Badge>
                        <span>{appliedPromotion.name?.en || appliedPromotion.name}</span>
                        {appliedPromotion.freeShipping && (
                          <Badge bg="info" className="ms-2">Free Shipping</Badge>
                        )}
                      </div>
                      <Button variant="outline-danger" size="sm" onClick={removePromotion}>
                        Remove
                      </Button>
                    </div>
                  )}
                  {promotionError && (
                    <Alert variant="danger" className="mt-2 mb-0 py-2">
                      {promotionError}
                    </Alert>
                  )}
                </Card.Body>
              </Card>

              <div className="d-flex justify-content-between">
                <span>Subtotal:</span>
                <span>{formatPrice(summary.subtotal)}</span>
              </div>
              <div className="d-flex justify-content-between">
                <span>Shipping:</span>
                <span>{summary.shipping === 0 ? 'FREE' : formatPrice(summary.shipping)}</span>
              </div>

              {/* Discount */}
              {appliedPromotion && (
                <div className="d-flex justify-content-between text-success">
                  <span>Discount ({appliedPromotion.code}):</span>
                  <span>- {formatPrice(promotionDiscount)}</span>
                </div>
              )}

              <div className="d-flex justify-content-between fw-bold fs-5">
                <span>Total:</span>
                <span>{formatPrice(finalTotal)}</span>
              </div>

              {summary.subtotal > 1000 && (
                <div className="text-success text-center mt-2">
                  <small>🎉 You qualify for free shipping!</small>
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default CheckoutPage;
