import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { ordersAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';
import LoadingSpinner from '../components/common/LoadingSpinner';
import Message from '../components/common/Message';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');

  const { formatPrice } = useApp();

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await ordersAPI.getOrders();
      setOrders(response.data.data);
    } catch (error) {
      setError('Failed to load orders');
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusVariant = (status) => {
    const variants = {
      pending: 'warning',
      confirmed: 'info',
      processing: 'primary',
      shipped: 'secondary',
      delivered: 'success',
      cancelled: 'danger',
      returned: 'dark'
    };
    return variants[status] || 'secondary';
  };

  const getPaymentStatusVariant = (status) => {
    const variants = {
      pending: 'warning',
      processing: 'info',
      completed: 'success',
      failed: 'danger',
      refunded: 'dark',
      cancelled: 'secondary'
    };
    return variants[status] || 'secondary';
  };

  const filteredOrders = orders.filter(order => {
    if (filter === 'all') return true;
    return order.status === filter;
  });

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) return <LoadingSpinner message="Loading your orders..." />;

  return (
    <Container className="py-4">
      <Row>
        <Col>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h1>My Orders</h1>
            <Form.Select 
              style={{ width: '200px' }}
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
            >
              <option value="all">All Orders</option>
              <option value="pending">Pending</option>
              <option value="confirmed">Confirmed</option>
              <option value="processing">Processing</option>
              <option value="shipped">Shipped</option>
              <option value="delivered">Delivered</option>
              <option value="cancelled">Cancelled</option>
            </Form.Select>
          </div>

          {error && <Message variant="danger">{error}</Message>}

          {filteredOrders.length === 0 ? (
            <Message variant="info">
              <div className="text-center py-4">
                <i className="fas fa-box-open fa-3x text-muted mb-3"></i>
                <h4>No orders found</h4>
                <p className="text-muted">
                  {filter === 'all' 
                    ? "You haven't placed any orders yet." 
                    : `No ${filter} orders found.`}
                </p>
                <Button as={Link} to="/products" variant="primary">
                  Start Shopping
                </Button>
              </div>
            </Message>
          ) : (
            <Card>
              <Card.Body className="p-0">
                <Table responsive hover className="mb-0">
                  <thead className="bg-light">
                    <tr>
                      <th>Order #</th>
                      <th>Date</th>
                      <th>Items</th>
                      <th>Total</th>
                      <th>Status</th>
                      <th>Payment</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredOrders.map(order => (
                      <tr key={order._id}>
                        <td>
                          <strong>{order.orderNumber}</strong>
                        </td>
                        <td>
                          <small>{formatDate(order.createdAt)}</small>
                        </td>
                        <td>
                          <div>
                            {order.items.slice(0, 2).map((item, index) => (
                              <div key={index} className="d-flex align-items-center mb-1">
                                <img 
                                  src={item.image} 
                                  alt={item.name.en}
                                  className="rounded me-2"
                                  style={{ width: '30px', height: '30px', objectFit: 'cover' }}
                                />
                                <small className="text-truncate" style={{ maxWidth: '150px' }}>
                                  {item.name.en}
                                </small>
                                {item.quantity > 1 && (
                                  <small className="text-muted ms-1">x{item.quantity}</small>
                                )}
                              </div>
                            ))}
                            {order.items.length > 2 && (
                              <small className="text-muted">
                                +{order.items.length - 2} more items
                              </small>
                            )}
                          </div>
                        </td>
                        <td>
                          <strong>{formatPrice(order.totalAmount)}</strong>
                        </td>
                        <td>
                          <Badge bg={getStatusVariant(order.status)}>
                            {order.status.replace('_', ' ').toUpperCase()}
                          </Badge>
                          {order.trackingNumber && (
                            <div>
                              <small className="text-muted">
                                Track: {order.trackingNumber}
                              </small>
                            </div>
                          )}
                        </td>
                        <td>
                          <Badge bg={getPaymentStatusVariant(order.paymentStatus)}>
                            {order.paymentStatus.replace('_', ' ').toUpperCase()}
                          </Badge>
                          <div>
                            <small className="text-muted text-capitalize">
                              {order.paymentMethod?.replace('_', ' ')}
                            </small>
                          </div>
                        </td>
                        <td>
                          <div className="d-flex gap-2">
                            <Button
                              as={Link}
                              to={`/order/${order._id}`}
                              variant="outline-primary"
                              size="sm"
                            >
                              View
                            </Button>
                            {order.status === 'pending' && (
                              <Button
                                variant="outline-danger"
                                size="sm"
                                onClick={async () => {
                                  if (window.confirm('Are you sure you want to cancel this order?')) {
                                    try {
                                      await ordersAPI.cancelOrder(order._id, 'Cancelled by customer');
                                      fetchOrders(); // Refresh orders
                                    } catch (error) {
                                      alert('Failed to cancel order');
                                    }
                                  }
                                }}
                              >
                                Cancel
                              </Button>
                            )}
                            {order.status === 'delivered' && (
                              <Button
                                variant="outline-success"
                                size="sm"
                                onClick={() => {
                                  // Implement reorder functionality
                                  alert('Reorder functionality coming soon!');
                                }}
                              >
                                Reorder
                              </Button>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          )}

          {/* Order Statistics */}
          {orders.length > 0 && (
            <Row className="mt-4">
              <Col md={3}>
                <Card className="text-center">
                  <Card.Body>
                    <h3>{orders.length}</h3>
                    <p className="text-muted mb-0">Total Orders</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="text-center">
                  <Card.Body>
                    <h3 className="text-success">
                      {orders.filter(o => o.status === 'delivered').length}
                    </h3>
                    <p className="text-muted mb-0">Delivered</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="text-center">
                  <Card.Body>
                    <h3 className="text-primary">
                      {orders.filter(o => ['pending', 'confirmed', 'processing'].includes(o.status)).length}
                    </h3>
                    <p className="text-muted mb-0">In Progress</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3}>
                <Card className="text-center">
                  <Card.Body>
                    <h3 className="text-danger">
                      {orders.filter(o => o.status === 'cancelled').length}
                    </h3>
                    <p className="text-muted mb-0">Cancelled</p>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default OrdersPage;
