import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, Form, Modal, ListGroup, ProgressBar } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { ordersAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';
import LoadingSpinner from '../components/common/LoadingSpinner';
import Message from '../components/common/Message';
import '../styles/ordersPage.css';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [trackingOrder, setTrackingOrder] = useState(null);
  const [showTrackingModal, setShowTrackingModal] = useState(false);
  const [copyFeedback, setCopyFeedback] = useState('');

  const { formatPrice } = useApp();

  useEffect(() => {
    fetchOrders();
  }, []);

  useEffect(() => {
    if (!copyFeedback) return undefined;
    const timeout = setTimeout(() => setCopyFeedback(''), 2500);
    return () => clearTimeout(timeout);
  }, [copyFeedback]);

  const fetchOrders = async () => {
    try {
      const response = await ordersAPI.getOrders();
      setOrders(response.data.data);
    } catch (error) {
      setError('Failed to load orders');
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusVariant = (status) => {
    const variants = {
      pending: 'warning',
      confirmed: 'info',
      processing: 'primary',
      shipped: 'secondary',
      delivered: 'success',
      cancelled: 'danger',
      returned: 'dark'
    };
    return variants[status] || 'secondary';
  };

  const getPaymentStatusVariant = (status) => {
    const variants = {
      pending: 'warning',
      processing: 'info',
      completed: 'success',
      failed: 'danger',
      refunded: 'dark',
      cancelled: 'secondary'
    };
    return variants[status] || 'secondary';
  };

  const filteredOrders = orders.filter(order => {
    if (filter === 'all') return true;
    return order.status === filter;
  });

  const totalOrders = orders.length;
  const deliveredCount = orders.filter(o => o.status === 'delivered').length;
  const inProgressCount = orders.filter(o => ['pending', 'confirmed', 'processing', 'shipped'].includes(o.status)).length;
  const cancelledCount = orders.filter(o => o.status === 'cancelled').length;

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatDateOnly = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const openTrackingModal = (order) => {
    setTrackingOrder(order);
    setShowTrackingModal(true);
    setCopyFeedback('');
  };

  const closeTrackingModal = () => {
    setShowTrackingModal(false);
    setTrackingOrder(null);
    setCopyFeedback('');
  };

  const handleCopyTrackingNumber = async (trackingNumber) => {
    if (!trackingNumber) return;

    try {
      if (typeof navigator !== 'undefined' && navigator.clipboard && navigator.clipboard.writeText) {
        await navigator.clipboard.writeText(trackingNumber);
        setCopyFeedback('Tracking number copied!');
      } else {
        throw new Error('Clipboard API not available');
      }
    } catch (err) {
      console.warn('Clipboard copy failed, falling back to prompt.', err);
      window.prompt('Copy the tracking number:', trackingNumber);
      setCopyFeedback('Copied manually.');
    }
  };

  const buildTrackingDetails = (order) => {
    if (!order) {
      return { steps: [], currentIndex: -1 };
    }

    const steps = [
      {
        key: 'pending',
        label: 'Order Placed',
        description: 'We received your order and sent a confirmation email.'
      },
      {
        key: 'confirmed',
        label: 'Order Confirmed',
        description: 'A seller confirmed item availability.'
      },
      {
        key: 'processing',
        label: 'Preparing Order',
        description: 'Items are being packaged for shipment.'
      },
      {
        key: 'shipped',
        label: 'Shipped',
        description: 'Your package is on the way to the delivery address.'
      },
      {
        key: 'delivered',
        label: 'Delivered',
        description: 'The package arrived at its destination.'
      }
    ];

    if (order.status === 'cancelled') {
      steps.push({
        key: 'cancelled',
        label: 'Order Cancelled',
        description: order.cancellationReason || 'The order was cancelled.',
        variant: 'danger'
      });
    } else if (order.status === 'returned') {
      steps.push({
        key: 'returned',
        label: 'Return Completed',
        description: order.returnReason || 'The order was returned and processed.',
        variant: 'warning'
      });
    }

    const currentIndex = steps.findIndex(step => step.key === order.status);
    const effectiveIndex = currentIndex >= 0 ? currentIndex : steps.findIndex(step => step.key === 'delivered');

    const decoratedSteps = steps.map((step, index) => {
      let state = 'upcoming';
      if (effectiveIndex < 0) {
        state = index === 0 ? 'current' : 'upcoming';
      } else if (index < effectiveIndex) {
        state = 'completed';
      } else if (index === effectiveIndex) {
        state = order.status === 'cancelled' ? 'cancelled' : order.status === 'returned' ? 'returned' : 'current';
      }
      return {
        ...step,
        state
      };
    });

    return { steps: decoratedSteps, currentIndex: effectiveIndex };
  };

  const renderStepIcon = (step) => {
    if (step.state === 'cancelled') {
      return <i className="fas fa-ban text-danger me-2"></i>;
    }
    if (step.state === 'returned') {
      return <i className="fas fa-undo text-warning me-2"></i>;
    }
    if (step.state === 'completed') {
      return <i className="fas fa-check-circle text-success me-2"></i>;
    }
    if (step.state === 'current') {
      return <i className="fas fa-dot-circle text-primary me-2"></i>;
    }
    return <i className="far fa-circle text-muted me-2"></i>;
  };

  if (loading) return <LoadingSpinner message="Loading your orders..." />;

  return (
    <Container className="orders-page-container py-5">
      <Row>
        <Col>
          <div className="orders-page-header">
            <div className="orders-page-header-content">
              <span className="orders-page-eyebrow">Track &amp; manage your purchases</span>
              <h1 className="orders-page-title">My Orders</h1>
              <p className="orders-page-subtitle">
                Keep tabs on every delivery, check payment updates, and quickly revisit past favorites.
              </p>
              {orders.length > 0 && (
                <div className="orders-page-highlights">
                  <span className="orders-page-chip">
                    <i className="fas fa-box"></i>
                    {totalOrders} total orders
                  </span>
                  <span className="orders-page-chip">
                    <i className="fas fa-check"></i>
                    {deliveredCount} delivered successfully
                  </span>
                  <span className="orders-page-chip">
                    <i className="fas fa-shipping-fast"></i>
                    {inProgressCount} currently in motion
                  </span>
                  {cancelledCount > 0 && (
                    <span className="orders-page-chip muted">
                      <i className="fas fa-ban"></i>
                      {cancelledCount} cancelled
                    </span>
                  )}
                </div>
              )}
            </div>
            <div className="orders-page-header-actions">
              <Form.Select
                className="orders-page-filter"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
              >
                <option value="all">All Orders</option>
                <option value="pending">Pending</option>
                <option value="confirmed">Confirmed</option>
                <option value="processing">Processing</option>
                <option value="shipped">Shipped</option>
                <option value="delivered">Delivered</option>
                <option value="cancelled">Cancelled</option>
              </Form.Select>
              <Button as={Link} to="/products" variant="primary" className="orders-page-shop-btn">
                Discover Products
              </Button>
            </div>
          </div>

          {error && (
            <div className="orders-page-alert">
              <Message variant="danger">{error}</Message>
            </div>
          )}

          {filteredOrders.length === 0 ? (
            <div className="orders-page-empty">
              <Message variant="info">
                <div className="orders-page-empty-card">
                  <i className="fas fa-box-open"></i>
                  <h4>No orders found</h4>
                  <p>
                    {filter === 'all'
                      ? "You haven't placed any orders yet."
                      : `No ${filter} orders found.`}
                  </p>
                  <Button as={Link} to="/products" variant="primary" className="orders-page-empty-btn">
                    Start Shopping
                  </Button>
                </div>
              </Message>
            </div>
          ) : (
            <Card className="orders-page-table-card">
              <Card.Body className="orders-page-table-wrapper">
                <Table responsive hover className="orders-page-table mb-0">
                  <thead>
                    <tr>
                      <th>Order #</th>
                      <th>Date</th>
                      <th>Items</th>
                      <th>Total</th>
                      <th>Status</th>
                      <th>Payment</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredOrders.map(order => (
                      <tr key={order._id}>
                        <td>
                          <span className="orders-page-order-number">{order.orderNumber}</span>
                        </td>
                        <td>
                          <small className="orders-page-date">{formatDate(order.createdAt)}</small>
                        </td>
                        <td>
                          <div className="orders-page-item-preview">
                            {order.items.slice(0, 2).map((item, index) => (
                              <div key={index} className="orders-page-item-row">
                                <img
                                  src={item.image}
                                  alt={item.name.en}
                                  className="orders-page-item-thumb"
                                />
                                <small className="orders-page-item-name">
                                  {item.name.en}
                                </small>
                                {item.quantity > 1 && (
                                  <small className="orders-page-item-quantity">x{item.quantity}</small>
                                )}
                              </div>
                            ))}
                            {order.items.length > 2 && (
                              <small className="orders-page-more-items">
                                +{order.items.length - 2} more items
                              </small>
                            )}
                          </div>
                        </td>
                        <td>
                          <strong className="orders-page-total">{formatPrice(order.totalAmount)}</strong>
                        </td>
                        <td>
                          <Badge bg={getStatusVariant(order.status)} className="orders-page-status-badge">
                            {order.status.replace('_', ' ').toUpperCase()}
                          </Badge>
                          {order.trackingNumber && (
                            <div>
                              <small className="orders-page-tracking">Track: {order.trackingNumber}</small>
                            </div>
                          )}
                        </td>
                        <td>
                          <Badge bg={getPaymentStatusVariant(order.paymentStatus)} className="orders-page-payment-badge">
                            {order.paymentStatus.replace('_', ' ').toUpperCase()}
                          </Badge>
                          <div>
                            <small className="orders-page-payment-method text-capitalize">
                              {order.paymentMethod?.replace('_', ' ')}
                            </small>
                          </div>
                        </td>
                        <td>
                          <div className="orders-page-actions">
                            <Button
                              as={Link}
                              to={`/order/${order._id}`}
                              variant="outline-primary"
                              size="sm"
                              className="orders-page-action-btn"
                            >
                              View
                            </Button>
                            {order.trackingNumber && (
                              <Button
                                variant="outline-secondary"
                                size="sm"
                                className="orders-page-action-btn"
                                onClick={() => openTrackingModal(order)}
                              >
                                Track Package
                              </Button>
                            )}
                            {order.status === 'pending' && (
                              <Button
                                variant="outline-danger"
                                size="sm"
                                className="orders-page-action-btn"
                                onClick={async () => {
                                  if (window.confirm('Are you sure you want to cancel this order?')) {
                                    try {
                                      await ordersAPI.cancelOrder(order._id, 'Cancelled by customer');
                                      fetchOrders();
                                    } catch (error) {
                                      alert('Failed to cancel order');
                                    }
                                  }
                                }}
                              >
                                Cancel
                              </Button>
                            )}
                            {order.status === 'delivered' && (
                              <Button
                                variant="outline-success"
                                size="sm"
                                className="orders-page-action-btn"
                                onClick={() => {
                                  alert('Reorder functionality coming soon!');
                                }}
                              >
                                Reorder
                              </Button>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          )}

          {orders.length > 0 && (
            <Row className="orders-page-stats">
              <Col md={3} sm={6}>
                <Card className="orders-page-stat-card">
                  <Card.Body>
                    <span className="orders-page-stat-label">Total Orders</span>
                    <h3 className="orders-page-stat-value">{totalOrders}</h3>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3} sm={6}>
                <Card className="orders-page-stat-card">
                  <Card.Body>
                    <span className="orders-page-stat-label">Delivered</span>
                    <h3 className="orders-page-stat-value text-success">{deliveredCount}</h3>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3} sm={6}>
                <Card className="orders-page-stat-card">
                  <Card.Body>
                    <span className="orders-page-stat-label">In Progress</span>
                    <h3 className="orders-page-stat-value text-primary">{inProgressCount}</h3>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={3} sm={6}>
                <Card className="orders-page-stat-card">
                  <Card.Body>
                    <span className="orders-page-stat-label">Cancelled</span>
                    <h3 className="orders-page-stat-value text-danger">{cancelledCount}</h3>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          )}
        </Col>
      </Row>

      <Modal
        show={showTrackingModal}
        onHide={closeTrackingModal}
        centered
        size="lg"
        contentClassName="orders-page-tracking-modal"
      >
        <Modal.Header closeButton className="orders-page-tracking-header">
          <Modal.Title>Track Shipment</Modal.Title>
        </Modal.Header>
        <Modal.Body className="orders-page-tracking-body">
          {trackingOrder && (
            <>
              <Row className="mb-3">
                <Col md={6} className="mb-3">
                  <p className="orders-page-label">Tracking Number</p>
                  <div className="orders-page-tracking-number">
                    <h5 className="mb-0">{trackingOrder.trackingNumber}</h5>
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      className="orders-page-copy-btn"
                      onClick={() => handleCopyTrackingNumber(trackingOrder.trackingNumber)}
                    >
                      Copy
                    </Button>
                    {copyFeedback && (
                      <small className="orders-page-copy-feedback">{copyFeedback}</small>
                    )}
                  </div>
                </Col>
                <Col md={3} className="mb-3">
                  <p className="orders-page-label">Carrier</p>
                  <div>{trackingOrder.carrier || 'Not provided'}</div>
                </Col>
                <Col md={3} className="mb-3">
                  <p className="orders-page-label">Shipping Method</p>
                  <div className="text-capitalize">{trackingOrder.shippingMethod || 'Standard'}</div>
                </Col>
              </Row>

              <Row className="mb-4">
                <Col md={4} className="mb-3">
                  <p className="orders-page-label">Order Status</p>
                  <Badge bg={getStatusVariant(trackingOrder.status)} className="orders-page-status-pill">
                    {trackingOrder.status.replace('_', ' ').toUpperCase()}
                  </Badge>
                </Col>
                <Col md={4} className="mb-3">
                  <p className="orders-page-label">Placed On</p>
                  <div>{formatDateOnly(trackingOrder.createdAt)}</div>
                </Col>
                <Col md={4} className="mb-3">
                  <p className="orders-page-label">Estimated Delivery</p>
                  <div>
                    {trackingOrder.estimatedDelivery
                      ? formatDateOnly(trackingOrder.estimatedDelivery)
                      : 'Pending update'}
                  </div>
                </Col>
              </Row>

              {(() => {
                const { steps, currentIndex } = buildTrackingDetails(trackingOrder);
                const progressPercent = steps.length
                  ? Math.min(100, Math.round(((currentIndex >= 0 ? currentIndex + 1 : 1) / steps.length) * 100))
                  : 0;

                return (
                  <>
                    <div className="orders-page-progress-wrapper">
                      <ProgressBar
                        now={progressPercent}
                        label={`${progressPercent}%`}
                        visuallyHidden={progressPercent === 0}
                        className="orders-page-progress"
                      />
                      {progressPercent === 0 && (
                        <small className="orders-page-progress-hint">
                          Tracking updates will appear once your order is processed.
                        </small>
                      )}
                    </div>
                    <ListGroup variant="flush" className="orders-page-steps">
                      {steps.map(step => (
                        <ListGroup.Item key={step.key} className="orders-page-step-item">
                          <div className="orders-page-step-icon">
                            {renderStepIcon(step)}
                          </div>
                          <div>
                            <div className="orders-page-step-title">
                              {step.label}
                              {step.state === 'cancelled' && (
                                <Badge bg="danger" className="orders-page-step-badge">Cancelled</Badge>
                              )}
                              {step.state === 'returned' && (
                                <Badge bg="warning" text="dark" className="orders-page-step-badge">Returned</Badge>
                              )}
                              {step.state === 'current' && step.key !== 'delivered' && step.key !== 'shipped' && (
                                <Badge bg="primary" className="orders-page-step-badge">In Progress</Badge>
                              )}
                            </div>
                            <small className="orders-page-step-description">{step.description}</small>
                          </div>
                        </ListGroup.Item>
                      ))}
                    </ListGroup>
                  </>
                );
              })()}
            </>
          )}
        </Modal.Body>
        <Modal.Footer className="orders-page-tracking-footer">
          <Button variant="secondary" onClick={closeTrackingModal} className="orders-page-close-btn">
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default OrdersPage;
