import React, { useState, useEffect } from 'react';
import { Container, Card, Form, Row, Col, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { categoriesAPI } from '../../services/api';
import LoadingSpinner from '../../components/common/LoadingSpinner';

const AdminCategoryNewPage = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({
    name: { en: '', am: '' },
    description: { en: '', am: '' },
    code: '',
    parent: '',
    displayOrder: 0
  });

  const navigate = useNavigate();

  useEffect(() => {
    const load = async () => {
      try {
        const res = await categoriesAPI.getCategories();
        const list = res.data?.data || res.data || [];
        setCategories(list);
      } catch (err) {
        // ignore
      }
    };
    load();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      // Ensure parent is null when empty string to avoid Mongoose CastError
      const submitData = {
        ...formData,
        displayOrder: parseInt(formData.displayOrder) || 0,
        parent: formData.parent && formData.parent !== '' ? formData.parent : null
      };
      await categoriesAPI.createCategory(submitData);
      navigate('/admin/categories');
    } catch (err) {
      console.error('Create category failed', err, err.response && err.response.data);
      // Backend may return { error: '...' } or { message: '...' }
      const serverErr = err.response?.data?.error || err.response?.data?.message || err.response?.data;
      setError(serverErr || err.message || 'Failed to create category');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <LoadingSpinner message="Creating category..." />;

  return (
    <Container fluid>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h1>Create Category</h1>
          <p className="text-muted mb-0">Add a new product category</p>
        </div>
      </div>

      {error && <Alert variant="danger">{error}</Alert>}

      <Card>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>English Name *</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.name.en}
                    onChange={(e) => setFormData(prev => ({ ...prev, name: { ...prev.name, en: e.target.value } }))}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Amharic Name *</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.name.am}
                    onChange={(e) => setFormData(prev => ({ ...prev, name: { ...prev.name, am: e.target.value } }))}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Category Code *</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.code}
                    onChange={(e) => setFormData(prev => ({ ...prev, code: e.target.value.toUpperCase() }))}
                    required
                    maxLength={10}
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Display Order</Form.Label>
                  <Form.Control
                    type="number"
                    value={formData.displayOrder}
                    onChange={(e) => setFormData(prev => ({ ...prev, displayOrder: e.target.value }))}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Parent Category</Form.Label>
              <Form.Select
                value={formData.parent}
                onChange={(e) => setFormData(prev => ({ ...prev, parent: e.target.value }))}
              >
                <option value="">No Parent (Main Category)</option>
                {categories.filter(cat => !cat.parent).map(cat => (
                  <option key={cat._id} value={cat._id}>{cat.name?.en || cat.name}</option>
                ))}
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>English Description</Form.Label>
              <Form.Control as="textarea" rows={2} value={formData.description.en}
                onChange={(e) => setFormData(prev => ({ ...prev, description: { ...prev.description, en: e.target.value } }))}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Amharic Description</Form.Label>
              <Form.Control as="textarea" rows={2} value={formData.description.am}
                onChange={(e) => setFormData(prev => ({ ...prev, description: { ...prev.description, am: e.target.value } }))}
              />
            </Form.Group>

            <div className="d-flex gap-2">
              <Button variant="secondary" onClick={() => navigate('/admin/categories')}>Cancel</Button>
              <Button variant="primary" type="submit">Create Category</Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default AdminCategoryNewPage;
