import React, { useMemo, useState } from 'react';
import { Container, Row, Col, Card, Table, Badge, Button, ProgressBar, Form, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const inventorySeed = [
  {
    id: 'inv-001',
    name: 'Shega Leather Tote',
    sku: 'BAG-2024-01',
    stock: 24,
    safetyStock: 15,
    reserved: 6,
    incoming: 12,
    status: 'healthy'
  },
  {
    id: 'inv-002',
    name: 'Addis Coffee Beans 1kg',
    sku: 'FOOD-ET-09',
    stock: 8,
    safetyStock: 20,
    reserved: 4,
    incoming: 30,
    status: 'low'
  },
  {
    id: 'inv-003',
    name: 'Abay Smart Watch',
    sku: 'ELEC-551',
    stock: 0,
    safetyStock: 10,
    reserved: 0,
    incoming: 15,
    status: 'critical'
  },
  {
    id: 'inv-004',
    name: 'Lalibela Handwoven Scarf',
    sku: 'TEXT-778',
    stock: 46,
    safetyStock: 25,
    reserved: 3,
    incoming: 0,
    status: 'healthy'
  }
];

const restockSeed = [
  {
    id: 'restock-001',
    product: 'Addis Coffee Beans 1kg',
    suggestedQty: 40,
    leadTime: '4 days',
    supplier: 'Buna Trading PLC'
  },
  {
    id: 'restock-002',
    product: 'Abay Smart Watch',
    suggestedQty: 30,
    leadTime: '7 days',
    supplier: 'E-Tech Imports'
  }
];

const statusVariant = {
  healthy: { label: 'Healthy', variant: 'success' },
  low: { label: 'Low', variant: 'warning' },
  critical: { label: 'Critical', variant: 'danger' }
};

const SellerInventoryPage = () => {
  const [inventory, setInventory] = useState(inventorySeed);
  const [restockQueue, setRestockQueue] = useState(restockSeed);
  const [selectedId, setSelectedId] = useState(null);
  const [message, setMessage] = useState('');
  const selectedItem = useMemo(
    () => inventory.find(item => item.id === selectedId) || null,
    [inventory, selectedId]
  );
  const [formState, setFormState] = useState({ quantity: '', reason: 'restock', note: '' });

  const handleSelectItem = (itemId) => {
    setSelectedId(itemId);
    const item = inventory.find(row => row.id === itemId);
    if (!item) return;
    setFormState({
      quantity: String(item.stock),
      reason: item.stock <= item.safetyStock ? 'restock' : 'adjustment',
      note: ''
    });
  };

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormState(prev => ({ ...prev, [name]: value }));
  };

  const handleUpdateInventory = (event) => {
    event.preventDefault();
    if (!selectedItem) return;
    const quantity = Number(formState.quantity);
    if (Number.isNaN(quantity) || quantity < 0) {
      setMessage('Please provide a valid quantity.');
      return;
    }

    setInventory(prev =>
      prev.map(item =>
        item.id === selectedItem.id
          ? {
              ...item,
              stock: quantity,
              status:
                quantity === 0
                  ? 'critical'
                  : quantity < item.safetyStock
                  ? 'low'
                  : 'healthy'
            }
          : item
      )
    );
    setMessage('Inventory updated successfully.');
  };

  const handleConvertToPurchaseOrder = (entryId) => {
    const entry = restockQueue.find(row => row.id === entryId);
    if (!entry) return;
    setMessage(`Purchase order created for ${entry.product} (${entry.suggestedQty} units).`);
    setRestockQueue(prev => prev.filter(row => row.id !== entryId));
  };

  return (
    <Container fluid className="py-4">
      <Row className="mb-4">
        <Col>
          <h2 className="fw-semibold mb-1">Inventory Control Center</h2>
          <p className="text-muted mb-0">
            Monitor stock levels, manage replenishment, and keep your best sellers available.
          </p>
        </Col>
        <Col xs="auto" className="d-flex align-items-center">
          <Button as={Link} to="/seller/products/add" variant="primary" className="shadow-sm">
            <i className="fas fa-plus me-2"></i>
            Add New Product
          </Button>
        </Col>
      </Row>

      {message && (
        <Alert variant="success" dismissible onClose={() => setMessage('')} className="shadow-sm">
          {message}
        </Alert>
      )}

      <Row className="g-4 align-items-stretch">
        <Col xl={8} className="d-flex flex-column">
          <Card className="h-100 shadow-sm">
            <Card.Header className="bg-white d-flex justify-content-between align-items-center">
              <div>
                <h5 className="mb-0">Live Inventory</h5>
                <small className="text-muted">Tap a product row to adjust its availability.</small>
              </div>
              <Badge bg="info">{inventory.length} SKUs</Badge>
            </Card.Header>
            <Card.Body className="p-0">
              <Table hover responsive className="mb-0 align-middle">
                <thead className="bg-light">
                  <tr>
                    <th>Product</th>
                    <th>SKU</th>
                    <th className="text-center">On Hand</th>
                    <th className="text-center">Reserved</th>
                    <th className="text-center">Incoming</th>
                    <th className="text-center">Coverage</th>
                    <th className="text-center">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {inventory.map(item => {
                    const coverage = item.safetyStock
                      ? Math.min(100, Math.round((item.stock / item.safetyStock) * 100))
                      : 100;
                    const status = statusVariant[item.status] || statusVariant.healthy;
                    return (
                      <tr
                        key={item.id}
                        role="button"
                        className={selectedId === item.id ? 'table-primary' : ''}
                        onClick={() => handleSelectItem(item.id)}
                      >
                        <td className="fw-semibold">{item.name}</td>
                        <td className="text-muted">{item.sku}</td>
                        <td className="text-center">{item.stock}</td>
                        <td className="text-center">{item.reserved}</td>
                        <td className="text-center">{item.incoming}</td>
                        <td>
                          <ProgressBar
                            now={coverage}
                            variant={coverage < 40 ? 'danger' : coverage < 75 ? 'warning' : 'success'}
                            label={`${coverage}%`}
                          />
                        </td>
                        <td className="text-center">
                          <Badge bg={status.variant}>{status.label}</Badge>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        </Col>

        <Col xl={4} className="d-flex flex-column">
          <Card className="mb-4 shadow-sm">
            <Card.Header className="bg-white">
              <h5 className="mb-0">Adjust Stock</h5>
            </Card.Header>
            <Card.Body>
              {selectedItem ? (
                <Form onSubmit={handleUpdateInventory}>
                  <div className="mb-3">
                    <div className="d-flex justify-content-between align-items-center">
                      <h6 className="mb-1">{selectedItem.name}</h6>
                      <Badge bg={statusVariant[selectedItem.status]?.variant || 'secondary'}>
                        {statusVariant[selectedItem.status]?.label || 'Status Unknown'}
                      </Badge>
                    </div>
                    <small className="text-muted">SKU: {selectedItem.sku}</small>
                  </div>

                  <Form.Group className="mb-3">
                    <Form.Label>Available Quantity</Form.Label>
                    <Form.Control
                      type="number"
                      name="quantity"
                      min={0}
                      value={formState.quantity}
                      onChange={handleFormChange}
                      required
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Reason</Form.Label>
                    <Form.Select name="reason" value={formState.reason} onChange={handleFormChange}>
                      <option value="restock">Restock received</option>
                      <option value="adjustment">Manual adjustment</option>
                      <option value="return">Customer return</option>
                      <option value="damage">Damaged or lost</option>
                    </Form.Select>
                  </Form.Group>

                  <Form.Group className="mb-4">
                    <Form.Label>Notes</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={3}
                      name="note"
                      value={formState.note}
                      placeholder="Add context for your team..."
                      onChange={handleFormChange}
                    />
                  </Form.Group>

                  <div className="d-grid gap-2">
                    <Button type="submit" variant="success" className="shadow-sm">
                      <i className="fas fa-save me-2"></i>
                      Apply Update
                    </Button>
                    <Button variant="outline-secondary" onClick={() => setSelectedId(null)}>
                      Cancel
                    </Button>
                  </div>
                </Form>
              ) : (
                <div className="text-center py-4 text-muted">
                  <i className="fas fa-clipboard-list fa-2x mb-3"></i>
                  <p className="mb-0">Select a product row to begin adjusting its stock.</p>
                </div>
              )}
            </Card.Body>
          </Card>

          <Card className="shadow-sm flex-grow-1">
            <Card.Header className="bg-white d-flex justify-content-between align-items-center">
              <div>
                <h5 className="mb-0">Restock Suggestions</h5>
                <small className="text-muted">Convert forecasts into purchase orders.</small>
              </div>
              <Badge bg="warning" text="dark">{restockQueue.length}</Badge>
            </Card.Header>
            <Card.Body className="p-0">
              {restockQueue.length === 0 ? (
                <div className="text-center py-4 text-muted">
                  <i className="fas fa-check-circle fa-2x mb-3 text-success"></i>
                  <p className="mb-0">No items need replenishment today.</p>
                </div>
              ) : (
                <Table hover responsive className="mb-0">
                  <thead className="bg-light">
                    <tr>
                      <th>Product</th>
                      <th className="text-center">Suggested</th>
                      <th>Lead Time</th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    {restockQueue.map(entry => (
                      <tr key={entry.id}>
                        <td>
                          <div className="fw-semibold">{entry.product}</div>
                          <small className="text-muted">Supplier: {entry.supplier}</small>
                        </td>
                        <td className="text-center">{entry.suggestedQty}</td>
                        <td>{entry.leadTime}</td>
                        <td className="text-end">
                          <Button
                            size="sm"
                            variant="outline-primary"
                            onClick={() => handleConvertToPurchaseOrder(entry.id)}
                          >
                            Create PO
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default SellerInventoryPage;
