import React from 'react';
import { Card, Button, Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useCart } from '../../contexts/CartContext';
import { useApp } from '../../contexts/AppContext';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { formatPrice, t, language } = useApp();

  const handleAddToCart = () => {
    addToCart(product, 1);
  };

  const displayName = product.name[language] || product.name.en;
  const displayDescription = product.description[language] || product.description.en;

  return (
    <Card className="h-100 product-card">
      <Link to={`/product/${product._id}`} className="text-decoration-none">
        <Card.Img 
          variant="top" 
          src={product.images[0]?.url || '/images/placeholder.jpg'} 
          alt={displayName}
          style={{ height: '200px', objectFit: 'cover' }}
        />
      </Link>
      
      <Card.Body className="d-flex flex-column">
        <Link to={`/product/${product._id}`} className="text-decoration-none text-dark">
          <Card.Title className="h6" style={{ minHeight: '48px' }}>
            {displayName}
          </Card.Title>
        </Link>
        
        <Card.Text className="text-muted small flex-grow-1">
          {displayDescription.substring(0, 100)}...
        </Card.Text>
        
        <div className="mt-auto">
          <div className="d-flex justify-content-between align-items-center mb-2">
            <span className="h5 text-primary mb-0">
              {formatPrice(product.price)}
            </span>
            {product.stock === 0 ? (
              <Badge bg="danger">Out of Stock</Badge>
            ) : product.stock < 10 ? (
              <Badge bg="warning">Low Stock</Badge>
            ) : (
              <Badge bg="success">In Stock</Badge>
            )}
          </div>
          
          <Button 
            variant="primary" 
            size="sm" 
            className="w-100"
            onClick={handleAddToCart}
            disabled={product.stock === 0}
          >
            {product.stock === 0 ? 'Out of Stock' : t('addToCart')}
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default ProductCard;
