import React from 'react';
import { Navbar, Nav, Container, NavDropdown, Badge } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useAuth } from '../../contexts/AuthContext';
import { useCart } from '../../contexts/CartContext';
import { useApp } from '../../contexts/AppContext';

const Header = () => {
  const { isAuthenticated, user, logout } = useAuth();
  const { getCartItemsCount } = useCart();
  const { language, updateLanguage, currency, updateCurrency, t } = useApp();

  const handleLogout = () => {
    logout();
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg" collapseOnSelect>
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand>
            <strong>EthioEcommerce</strong>
          </Navbar.Brand>
        </LinkContainer>

        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <LinkContainer to="/">
              <Nav.Link>{t('home')}</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/products">
              <Nav.Link>{t('products')}</Nav.Link>
            </LinkContainer>
          </Nav>

          <Nav className="ms-auto">
            {/* üåê Language Selector */}
            <NavDropdown
              title={language === 'en' ? 'English' : '·ä†·àõ·à≠·äõ'}
              id="language-dropdown"
            >
              <NavDropdown.Item onClick={() => updateLanguage('en')}>
                English
              </NavDropdown.Item>
              <NavDropdown.Item onClick={() => updateLanguage('am')}>
                ·ä†·àõ·à≠·äõ
              </NavDropdown.Item>
            </NavDropdown>

            {/* üí± Currency Selector */}
            <NavDropdown title={currency} id="currency-dropdown">
              <NavDropdown.Item onClick={() => updateCurrency('ETB')}>
                ETB
              </NavDropdown.Item>
              <NavDropdown.Item onClick={() => updateCurrency('USD')}>
                USD
              </NavDropdown.Item>
            </NavDropdown>

            {/* üõí Cart */}
            <LinkContainer to="/cart">
              <Nav.Link>
                <i className="fas fa-shopping-cart"></i> {t('cart')}
                {getCartItemsCount() > 0 && (
                  <Badge bg="success" className="ms-1">
                    {getCartItemsCount()}
                  </Badge>
                )}
              </Nav.Link>
            </LinkContainer>

            {/* üë§ User Dropdown */}
            {isAuthenticated ? (
              <NavDropdown title={user?.name} id="username">
                <LinkContainer to="/profile">
                  <NavDropdown.Item>
                    <i className="fas fa-user me-2"></i>Profile
                  </NavDropdown.Item>
                </LinkContainer>
                <LinkContainer to="/orders">
                  <NavDropdown.Item>
                    <i className="fas fa-shopping-bag me-2"></i>My Orders
                  </NavDropdown.Item>
                </LinkContainer>
                {user?.role === 'seller' && (
                  <LinkContainer to="/seller">
                    <NavDropdown.Item>
                      <i className="fas fa-store me-2"></i>Seller Dashboard
                    </NavDropdown.Item>
                  </LinkContainer>
                )}
                {user?.role === 'admin' && (
                  <LinkContainer to="/admin">
                    <NavDropdown.Item>
                      <i className="fas fa-cog me-2"></i>Admin Dashboard
                    </NavDropdown.Item>
                  </LinkContainer>
                )}
                <NavDropdown.Divider />
                <NavDropdown.Item onClick={handleLogout}>
                  <i className="fas fa-sign-out-alt me-2"></i>{t('logout')}
                </NavDropdown.Item>
              </NavDropdown>
            ) : (
              <LinkContainer to="/login">
                <Nav.Link>
                  <i className="fas fa-user"></i> {t('login')}
                </Nav.Link>
              </LinkContainer>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
